ecc_agent_script_file.script:
function Create-Apps {
    param (
        $Path,
        $Url,
        $Auth
    )
    $resourceTable = 'cmdb_ci_resource_group'
    $resourceField = 'object_id'
    $subTable = 'cmdb_ci_cloud_service_account'
    $subField = 'name'
    $subDisplay = 'account_id,name'
    $appTable = "cmdb_ci_service_auto"
    $field = 'name'
    $rg = Import-Csv -Path $path
    $rgGroups = $rg | Group-Object -Property @("APP (TAG)", "ENV (TAG)")
    $added = 0
    $missing = 0
    $ignored = 0
    $total = 0
    $missingBody = "";

    $missingNames = "";
    $ignoredNames = "";

    foreach ($group in $rgGroups){
        
        $subValues = '';
        $fullName = $group.Group[0].'APP (TAG)' + "-" + (Get-Culture).TextInfo.ToTitleCase($group.Group[0].'ENV (TAG)')
         foreach ($res in $group.Group){
            $subValues = $res.SUBSCRIPTION + ','
        }
        # get subscription ids and build map of account names to account ids

        $subRes = Get-CIByQuery -tableName $subTable -field $subField -values $subValues -baseUrl $Url -auth $Auth -display $subDisplay
        $subMap = @{}

        foreach ($sub in $subRes.result){
            $subKey = $sub.name
            $subMap.$subKey = $sub.account_id
        }
        #construct object ids for resource groups
        $resValues = '';
        foreach ($appRes in $group.Group){
            $resName = $appRes.NAME
	    $subName = $appRes.SUBSCRIPTION 	
	    $subId = $subMap.$subName
            if ($subId){
	    $resObjId = '/subscriptions/' + $subId + '/resourceGroups/' + $resName
            $resValues += $resObjId + ','
            }
        }
       
        #get resource group sys_id's by object_id
        $grRes = Get-CIByQuery -tableName $resourceTable -field $resourceField -values $resValues -baseUrl $Url -auth $Auth

        #get app id by name and construct body 

        $appRes = Get-CIByQuery -tableName $appTable -field $field -values $fullName  -baseUrl $Url -auth $Auth
        $outRels = @();
	$childIds = @();
        foreach ($id in $grRes.result){
            $outRels += @{
                'target' = $id.sys_id
                'type' = '1a9cb166f1571100a92eb60da2bce5c5'
            }
	    $childIds += $id.sys_id
        }
        $appBody = @{
            'outbound_relations' = $outRels
        }
        $appId = $appRes.result[0].sys_id
        $location = $Url + '/api/now/cmdb/instance/cmdb_ci_service_auto/' + $appId + "/relation"
        $authHeader = 'Basic ' + $auth
        $headers = @{
            'Authorization' = $authHeader
            'Content-Type' = 'application/json'
        }
        $jsonBody = $appBody | ConvertTo-Json
	if ($appId){
		$hasRels = checkRels -Uri $Url -auth $Auth -appId $appId -childIds $childIds
	}
	else {
		$hasRels = $true
	}
        
    
        #create app relationships and write log

	

        if ($hasRels -eq $false){
         Write-Host "App Id: "$appId "First Link:" $outRels[0].target post to $location
         $response = Invoke-RestMethod -Uri $location -Body $jsonBody -headers $headers -Method POST
         $added++
    }
        elseif (!$appId){
        Write-Host $fullName does not exist
        $missing++   
        $missingNames += $fullName + "`n"
	$missingBody += $resValues + ","
    }
        else {
        Write-Host Application $fullName already has outbound CI relationships defined
	$ignoredNames += $fullName + "`n"
        $ignored++
    }
    $total++
    }
    Write-Host "Total:" $total"," "Missing:" $missing"," "Ignored:" $ignored
    $path = ${pwd}.Path
    $filePath = $path + '\Missing App Names (Azure).txt';
    New-Item -Type File -Path $filePath -Force
    Set-Content -Path $filePath -Value $missingNames
    
    $filePath = $path + '\Ignored App Names (Azure).txt';
    New-Item -Type File -Path $filePath -Force
    Set-Content -Path $filePath -Value $ignoredNames

    $bodyPath = $path + '\Missing Resource Groups.txt'
    New-Item -Type File -Path $bodyPath -Force
    $missingRes = '';
    $missingBody -split "," | foreach-object {$missingRes += $_ + '`'}

    Set-Content -Path $bodyPath -Value $missingRes
    
}
    
    function Get-CIByQuery {
        param (
            $tableName,
            $field,
            $values,
            $baseUrl,
            $auth,
            $display
        )
        if ($values){
		if ($display){
            $restPath = '/api/now/table/' + $tableName + '?sysparm_fields=sys_id,' + $display + '&sysparm_query=' + $field + 'IN' + $values
        }
        else {
            $restPath = '/api/now/table/' + $tableName + '?sysparm_fields=sys_id&sysparm_query=' + $field + 'IN' + $values
        }
        
        $uri = $baseUrl + $restPath
        $authHeader = 'Basic ' + $auth
        $headers = @{
            'Authorization' = $authHeader;
            'Content-Type' = 'application/json';
            'Accept' = 'application/json';
        }
        Invoke-RestMethod -Uri $uri -headers $headers -Method GET
}
    }
    
    $Env = "prod"
    
    if ($Env -eq "dev"){
        $Uri = "https://bakerdev.service-now.com"
    }
    elseif ($Env -eq "qa"){
        $Uri = "https://bakerqa.service-now.com"
    }
    elseif ($Env -eq "uat"){
        $Uri = "https://bakeruat.service-now.com"
    }
    elseif ($Env -eq "prod") {
        $Uri= "https://bakerhughes.service-now.com"
    }
    
    
    function checkRels {
     param (
        $uri,
        $auth,
        $appId,
	$childIds
    )
    
    $headers = @{
        Authorization = 'Basic ' + $auth
    }
    
    $url = $uri + "/api/now/cmdbrelation/relation/" + $appId + "?sLevel=1"
    
    $res = Invoke-RestMethod -Uri $url -Headers $headers

    $rels = $res.result.relations | ConvertFrom-Json
    
    foreach ($rel in $rels.relations.relations){
	if ($rel.childSysClassName -eq "cmdb_ci_resource_group" -and -not ($rel.childSysId -in $childIds)){
		Write-Host APP SKIPPED: $appId
	    return $true
}
		
    }
    return $false   

    }
    
    
    Create-Apps -Path "C:\Users\hlib.korniienko\Desktop\Baker Hughes\Azure Service Graph\CMDB APIs\Prod SGC RG connections needed for connectiong to CMDB application.csv" -Url $Uri -Auth
