sys_data_source.data_loader:
(function loadData(import_set_table, data_source, import_log, last_success_import_time) {

	var provider = new sn_cc.ConnectionInfoProvider();
	var connectionInfo = provider.getConnectionInfo("d7db37212fdbd110d8a4d5f62799b6f0");
	var attributes = connectionInfo.getExtendedAttributes();
	var log = new GlideImportLog();
    try {
        var r = new sn_ws.RESTMessageV2('Jira Groups  Pull', 'Get Jira Groups');
		for (var attr in attributes){
			r.setStringParameterNoEscape(attr.replace('u_', ''), attributes[attr]);
		}
        var response = r.execute();
        var responseBody = JSON.parse(response.getBody());

        var httpStatus = response.getStatusCode();
		var groups = responseBody.groups.groups;
		for (var i = 0; i < groups.length && i < import_set_table.getMaximumRows(); i++){ // maximum rows is 20 in the case of test load
			import_set_table.insert(groups[i]);
		}
		log.info("Jjira groups load status code " + httpStatus, "Jira Groups Load");
    } catch (ex) {
        var message = ex.message; // most of the errors will end up as Syntax Errors anyway (RhinoECMAError)
		import_log.error(message);
    }

})(import_set_table, data_source, import_log, last_success_import_time);

