sys_variable_value.value:
(function execute(inputs, outputs) {
  var response = JSON.parse(inputs.body);
  var acrNum = response["d"]["ReqNum"];
  var acrStatus = response["d"]["ReqStatus"]
  outputs.req_num = acrNum;
  outputs.req_status = acrStatus;
})(inputs, outputs);

