sys_variable_value.value:
(function execute(inputs, outputs) {
  var headerList = JSON.parse(inputs.headers)
  var token = headerList['x-csrf-token'];
  var cookies = headerList['set-cookie']

if (JSUtil.nil(token)){
   throw "Could not retrieve token" 
}
else {
var sCookies = cookies.reduce(function (p, c) {
  var heads = c.split(';');
  for (var i in heads){
  var tokens = heads[i].split('=');
  if (tokens[0].trim() != 'path'){
   p.push(heads[i])

}

  }
return p

  }, []);


  outputs.sap_cookie = cookies.join('; ');
  outputs.csrf_token = token.toString();
}
})(inputs, outputs);

