sys_variable_value.value:
(function execute(inputs, outputs) {
    var gr = new GlideRecord('sc_req_item');
 	 var body = "";
 	 if (gr.get(inputs.id)){
	    body =  new SCTaskUtils(gr).getJSONBody(!inputs.flat);
     }
  outputs.body = JSON.stringify(body, null, 10)
})(inputs, outputs);

