function getType(question) {
    return new GlideQuery('item_option_new')
        .where("sys_id", question)
        .selectOne("type$DISPLAY")
        .get()["type$DISPLAY"];
}

function getValue(result) {
    if (JSUtil.isJavaObject(result)) {
        return j2js(result);
    } else {
        return result;
    }
}
