sys_script_include.script:
var IntegrationStep = Class.create();
IntegrationStep.prototype = {
    initialize: function(callback, inputs) {
		this.inputs = inputs; // initial inputs or result of callback
		this.logger = {}; // logger object
		this.runtime = {}; // runtime state for logging
		this.source = ""; // log source
		this.errorFlag = false; // should stop or continue step processing
		this.error = {}; // error object
    },

	withRetry: function () {
		if (errorFlag){
			// retry callback with given inputs 
		}
	},
	/*
		run the callback and save the output	

	*/
	_run: function () {},
	/*
	run integration step, log inputs, outputs, and runtime details
	*/
	then: function(_callback, _inputs) {
		if (!this.errorFlag){
			try {
			this.logger.log(_inputs, this.source, this.runtime);
			var payload = _inputs ? _inputs : this.inputs;
			var inputs = _callback.apply(this, payload);
			this.logger.log(inputs, this.source, this.runtime);
			return new IntegrationStep(inputs);
			} // if error flag, stop and handle the error
			catch (e){
				this.error = true;
				this._catch(e);
			}
			finally {
				this._finally();
			}
	}
		else {
			this._catch(this.error);
		}

	},

	_catch: function () {
	},

	_finally: function () {},

    type: 'IntegrationStep'
};
