sys_script_include.description:
//* Dont edit this script include. Best practise: Extend this script include and override the functions like shown in sample code. 
// Use the Script include to add or update headers, query parameters, endpoint URLs in oauth flow.
//* sample script below
//*
	var OAuthCustomUtil = Class.create();
	OAuthCustomUtil.prototype = Object.extendsObject(OAuthUtil, {

	preprocessAuthCode: function(requestParamMap) {
		this.oauthContext.setAuthorizationEndpoint("https://accounts.google.com/o/oauth2/v2/auth");
		this.oauthContext.addHeader("Content-Type","application/json");
		var profGr = this.oauthContext.getOAuthProfile();
		gs.info(profGr.getValue('name'));
	},
	
	preprocessAccessToken: function(requestParamMap) {
		this.oauthContext.addHeader("Content-Type","application/json");
		this.oauthContext.addQueryParameter("name","jackson");
		gs.info(this.oauthContext.getQueryParameter("name"));
		gs.info(this.oauthContext.getHeader("Content-Type"));
		this.oauthContext.setTokenEndpoint("https://oauth2.googleapis.com/token");
		var profGr = this.oauthContext.getOAuthProfile();
		gs.info(profGr.getValue('grant_type'));
	}
	type: 'OAuthCustomUtil'
});

sys_script_include.script:
//* Dont edit this script include. Best practise: Extend this script include and override the functions. 

var OAuthUtilWithRefreshV2 = Class.create();

OAuthUtilWithRefreshV2.prototype = Object.extendsObject(OAuthUtil, {

	preprocessAuthCode: function(requestParamMap) {
		this.oauthContext.addQueryParameter("access_type","offline");
		this.oauthContext.addQueryParameter("prompt","consent");

	},

	type: 'OAuthUtilWithRefreshV2'
});
