sys_script_include.script:
var CatVariableUtils = Class.create();
CatVariableUtils.prototype = Object.extendsObject(AbstractAjaxProcessor, {

	checkAccessRequests: function () {
		var item = this.getParameter('sysparm_item');
		var varsCsv = this.getParameter('sysparm_variables');
		var variables = varsCsv.split(';').reduce(function(p, c){
			var tokens = c.split('=');
			p[tokens[0]] = tokens[1];
			return p;
		}, {});
		return this.getReqItemByVariables(item, variables);
	},
	getReqItemByVariables: function(item, variables){
		var gr = new GlideRecord('sc_req_item');
		gr.addQuery('state', '!=', '7');
		gr.addQuery('state', '!=', '3');
		gr.addQuery('cat_item', item);
		gr.query();
		var vrs = Object.keys(variables);
		while(gr.next()){
			var count = 0;
			for (var variable in vrs){
				if (gr.variables[vrs[variable]] != variables[vrs[variable]]){
					break;
				}
				else {
					count++;
				}
			}
			if (count == vrs.length){
				return gr.number;
			}

		}
		return "";
	},
    getVariablesByQuestion: function(reqItemId, question) {
		var result = [];
        var gr = new GlideRecord('sc_req_item');
        gr.get(reqItemId);

        var catVars = Object.keys(gr.variables);

        for (var i in catVars) {
            if (gr.variables[catVars[i]].getLabel() == question){
				result.push(gr.variables[catVars[i]]);
			}
        }
		return result;
    },
    type: 'CatVariableUtils'
});
