sys_web_service.script:
(function scriptedWebServiceOperation(request, response) {

	// user must have soap_script role
	try {
		var inputs = {};
		inputs['request_data'] = soapRequestXML; // String 
		var result = sn_fd.FlowAPI.getRunner().subflow('global.algosec_sc_request').inForeground().withInputs(inputs).run();
		var outputs = result.getOutputs();
		response.request = outputs['request'];
		response.status = "success"; 
	} catch (ex) {
		response.status = "error";
		response.message = ex.getMessage();
	}
})(request, response);
